mapper.insert:
insert into T_MANAGE_AREA ( ID
, NAME
, PARENT_CODE
, UPDATED
, AREA_ORDER
 , AREA_TYPE
, OPERATOR
, CREATED
, DATA_TYPE
, CODE
)values(#{id,jdbcType=VARCHAR}
, #{name,jdbcType=VARCHAR}
, #{parentCode,jdbcType=INTEGER}
, #{updated,jdbcType=TIMESTAMP}
, #{areaOrder,jdbcType=INTEGER}
 , #{areaType,jdbcType=INTEGER}
, #{operator,jdbcType=VARCHAR}
, #{created,jdbcType=TIMESTAMP}
, #{dataType,jdbcType=INTEGER}
, #{code,jdbcType=INTEGER}
)

mapper.update:
update T_MANAGE_AREA set NAME=#{name,jdbcType=VARCHAR}
, PARENT_CODE=#{parentCode,jdbcType=INTEGER}
, UPDATED=#{updated,jdbcType=TIMESTAMP}
, AREA_ORDER=#{areaOrder,jdbcType=INTEGER}
 , AREA_TYPE=#{areaType,jdbcType=INTEGER}
, OPERATOR=#{operator,jdbcType=VARCHAR}
, DATA_TYPE=#{dataType,jdbcType=INTEGER}
, CODE=#{code,jdbcType=INTEGER}
where CODE=#{code,jdbcType=INTEGER}

mapper.select:
select NAME
, PARENT_CODE
, UPDATED
, AREA_ORDER
,AREA_TYPE
, OPERATOR
, CREATED
, DATA_TYPE
, CODE
from T_MANAGE_AREA


           and NAME = #{name,jdbcType=VARCHAR}


           and PARENT_CODE = #{parentCode,jdbcType=VARCHAR}


           and UPDATED = #{updated,jdbcType=TIMESTAMP}


           and AREA_ORDER = #{areaOrder,jdbcType=INTEGER}


           and AREA_TYPE = #{areaType,jdbcType=INTEGER}


           and OPERATOR = #{operator,jdbcType=VARCHAR}


           and CREATED = #{created,jdbcType=TIMESTAMP}


           and DATA_TYPE = #{dataType,jdbcType=INTEGER}


           and CODE = #{code,jdbcType=VARCHAR}


ORDER  BY AREA_TYPE, CODE,CREATED
