mapper.insert:

    select uuid()

insert into T_MANAGE_RESOURCE ( ID
, ICON
, NAME
, UPDATED
, IS_CAN_MODIFY
, REMARK
, OPERATOR
, CREATED
, URL
, RESOURCE_TYPE
, PARENT_ID
, APP_ID
)values(#{id,jdbcType=VARCHAR}
, #{icon,jdbcType=VARCHAR}
, #{name,jdbcType=VARCHAR}
, #{updated,jdbcType=TIMESTAMP}
, #{isCanModify,jdbcType=VARCHAR}
, #{remark,jdbcType=VARCHAR}
, #{operator,jdbcType=VARCHAR}
, #{created,jdbcType=TIMESTAMP}
, #{url,jdbcType=VARCHAR}
, #{resourceType,jdbcType=VARCHAR}
, #{parentId,jdbcType=VARCHAR}
, #{appId,jdbcType=VARCHAR}
)

mapper.select:
        select ICON
        , NAME
        , UPDATED
        , IS_CAN_MODIFY
        , ID
        , REMARK
        , OPERATOR
        , CREATED
        , URL
        , RESOURCE_TYPE
        , PARENT_ID
        , APP_ID
        from T_MANAGE_RESOURCE


                   and ICON = #{icon,jdbcType=VARCHAR}


                   and NAME = #{name,jdbcType=VARCHAR}


                   and UPDATED = #{updated,jdbcType=TIMESTAMP}


                   and IS_CAN_MODIFY = #{isCanModify,jdbcType=VARCHAR}


                   and ID = #{id,jdbcType=VARCHAR}


                   and REMARK = #{remark,jdbcType=VARCHAR}


                   and OPERATOR = #{operator,jdbcType=VARCHAR}


                   and CREATED = #{created,jdbcType=TIMESTAMP}


                   and URL = #{url,jdbcType=VARCHAR}


                   and RESOURCE_TYPE = #{resourceType,jdbcType=VARCHAR}


                   and PARENT_ID = #{parentId,jdbcType=VARCHAR}


                   and APP_ID = #{appId,jdbcType=VARCHAR}



                and id  IN(select resource_id from t_manage_role_resource where role_id
		      IN(select role_id from t_manage_user_role where user_id= #{userId,jdbcType=VARCHAR}
		      )
            )



        order BY CREATED
