mapper.insert:

    select uuid()

insert into T_MANAGE_ROLE_RESOURCE ( ID
, UPDATED
, AUTHORIZE_LEVEL
, OPERATOR
, CREATED
, RESOURCE_ID
, ROLE_ID
)values(#{id,jdbcType=VARCHAR}
, #{updated,jdbcType=TIMESTAMP}
, #{authorizeLevel,jdbcType=VARCHAR}
, #{operator,jdbcType=VARCHAR}
, #{created,jdbcType=TIMESTAMP}
, #{resourceId,jdbcType=VARCHAR}
, #{roleId,jdbcType=VARCHAR}
)

mapper.update:
update T_MANAGE_ROLE_RESOURCE set UPDATED=#{updated,jdbcType=TIMESTAMP}
, AUTHORIZE_LEVEL=#{authorizeLevel,jdbcType=VARCHAR}
, OPERATOR=#{operator,jdbcType=VARCHAR}
, CREATED=#{created,jdbcType=TIMESTAMP}
, RESOURCE_ID=#{resourceId,jdbcType=VARCHAR}
, ROLE_ID=#{roleId,jdbcType=VARCHAR}
where ID=#{id,jdbcType=VARCHAR}

mapper.select:
select UPDATED
, ID
, AUTHORIZE_LEVEL
, OPERATOR
, CREATED
, RESOURCE_ID
, ROLE_ID
from T_MANAGE_ROLE_RESOURCE


           and UPDATED = #{updated,jdbcType=TIMESTAMP}


           and ID = #{id,jdbcType=VARCHAR}


           and AUTHORIZE_LEVEL = #{authorizeLevel,jdbcType=VARCHAR}


           and OPERATOR = #{operator,jdbcType=VARCHAR}


           and CREATED = #{created,jdbcType=TIMESTAMP}


           and RESOURCE_ID = #{resourceId,jdbcType=VARCHAR}


           and ROLE_ID = #{roleId,jdbcType=VARCHAR}
