mapper.update:
update T_MANAGE_USER set
 LOGIN_COUNT=#{loginCount,jdbcType=INTEGER}
, AREA_CODE=#{areaCode,jdbcType=VARCHAR}
,LAST_LOGIN_TIME=#{lastLoginTime,jdbcType=TIMESTAMP}
, REAL_NAME=#{realName,jdbcType=VARCHAR}
, REMARK=#{remark,jdbcType=VARCHAR}
, LOCKED=#{locked,jdbcType=VARCHAR}
, ASK=#{ask,jdbcType=VARCHAR}
, ICON=#{icon,jdbcType=VARCHAR}
, MOBILE_NO=#{mobileNo,jdbcType=VARCHAR}
, PHONE_NO=#{phoneNo,jdbcType=VARCHAR}
, USER_NAME=#{userName,jdbcType=VARCHAR}
, UPDATED=#{updated,jdbcType=TIMESTAMP}
, EMAIL=#{email,jdbcType=VARCHAR}
, STR_KEY=#{strKey,jdbcType=VARCHAR}
, PASSWORD=#{password,jdbcType=VARCHAR}
 , ORGANIZATION_ID=#{organizationId,jdbcType=VARCHAR}
where ID=#{id,jdbcType=VARCHAR}

update T_MANAGE_USER set
 LOCKED=#{locked,jdbcType=VARCHAR}
  , UPDATED=#{updated,jdbcType=TIMESTAMP}
where ID=#{id,jdbcType=VARCHAR} AND IS_ADMINISTRATOR=0

mapper.select:
select IS_ADMINISTRATOR , LOGIN_COUNT , AREA_CODE , LAST_LOGIN_TIME , REAL_NAME , REMARK , LOCKED , OPERATOR , CREATED , ASK , ICON , MOBILE_NO , PHONE_NO , USER_NAME , UPDATED , ID , EMAIL , STR_KEY , PASSWORD ,ORGANIZATION_ID from T_MANAGE_USER
and ID = #{id,jdbcType=VARCHAR}
and USER_NAME = #{userName,jdbcType=VARCHAR}

select IS_ADMINISTRATOR
, LOGIN_COUNT
, AREA_CODE
, LAST_LOGIN_TIME
, REAL_NAME
, REMARK
, LOCKED
, OPERATOR
, CREATED
, ASK
, ICON
, MOBILE_NO
, PHONE_NO
, USER_NAME
, UPDATED
, ID
, EMAIL
, STR_KEY
, PASSWORD
,ORGANIZATION_ID
from T_MANAGE_USER


           and IS_ADMINISTRATOR = #{isAdministrator,jdbcType=VARCHAR}


           and LOGIN_COUNT = #{loginCount,jdbcType=INTEGER}


           and AREA_CODE = #{areaCode,jdbcType=VARCHAR}


           and LAST_LOGIN_TIME = #{lastLoginTime,jdbcType=TIMESTAMP}


           and REAL_NAME = #{realName,jdbcType=VARCHAR}


           and REMARK = #{remark,jdbcType=VARCHAR}


           and LOCKED = #{locked,jdbcType=VARCHAR}


           and OPERATOR = #{operator,jdbcType=VARCHAR}


           and CREATED = #{created,jdbcType=TIMESTAMP}


           and ASK = #{ask,jdbcType=VARCHAR}


           and ICON = #{icon,jdbcType=VARCHAR}


           and MOBILE_NO = #{mobileNo,jdbcType=VARCHAR}


           and PHONE_NO = #{phoneNo,jdbcType=VARCHAR}


         and USER_NAME LIKE #{userName,jdbcType=VARCHAR}"%"


           and UPDATED = #{updated,jdbcType=TIMESTAMP}


           and ID = #{id,jdbcType=VARCHAR}


           and EMAIL = #{email,jdbcType=VARCHAR}


           and STR_KEY = #{strKey,jdbcType=VARCHAR}


           and PASSWORD = #{password,jdbcType=VARCHAR}


limit #{start},#{limit}

select count(1) from T_MANAGE_USER


         and LOCKED=#{locked,jdbcType=INTEGER}


         and UPDATED=#{updated,jdbcType=TIMESTAMP}


         and USER_NAME LIKE #{userName,jdbcType=VARCHAR}"%"


         and PHONE_NO=#{phoneNo,jdbcType=VARCHAR}


         and AREA_CODE=#{areaCode,jdbcType=VARCHAR}


         and EMAIL=#{email,jdbcType=VARCHAR}


         and REAL_NAME=#{realName,jdbcType=VARCHAR}


         and LOGIN_COUNT=#{loginCount,jdbcType=INTEGER}


         and ORGANIZATION_ID=#{organizationId,jdbcType=VARCHAR}


         and MOBILE_NO=#{mobileNo,jdbcType=VARCHAR}


         and CREATED=#{created,jdbcType=TIMESTAMP}


         and PASSWORD=#{password,jdbcType=VARCHAR}


         and OPERATOR=#{operator,jdbcType=VARCHAR}


         and LAST_LOGIN_TIME=#{lastLoginTime,jdbcType=TIMESTAMP}


         and ASK=#{ask,jdbcType=VARCHAR}


         and ID=#{id,jdbcType=VARCHAR}


         and ICON=#{icon,jdbcType=VARCHAR}


         and STR_KEY=#{strKey,jdbcType=VARCHAR}


         and REMARK=#{remark,jdbcType=VARCHAR}


         and IS_ADMINISTRATOR=#{isAdministrator,jdbcType=INTEGER}
